mapper.select:
SELECT count(*) FROM ca_apply_check ac LEFT JOIN ca_apply a ON ac.apply_id = a.id WHERE 1=1
AND ac.check_status = #{activeCheckQueryDTO.checkStatus}
AND a.active_name LIKE concat('%',#{activeCheckQueryDTO.activeName},'%')

SELECT ac.id, ac.apply_id, ac.check_status, ac.check_content, ac.active_check_data, a.active_name, a.active_level, a.active_status FROM ca_apply_check ac LEFT JOIN ca_apply a ON ac.apply_id = a.id WHERE 1=1
AND ac.check_status = #{activeCheckQueryDTO.checkStatus}
AND a.active_name LIKE concat('%',#{activeCheckQueryDTO.activeName},'%')
ORDER BY CASE ac.check_status WHEN '${@com.qingfeng.cms.domain.apply.enums.ApplyCheckStatusEnum@INIT.name}' THEN 1 WHEN '${@com.qingfeng.cms.domain.apply.enums.ApplyCheckStatusEnum@IS_NOT_PASSED.name}' THEN 2 ELSE 3 END LIMIT #{activeCheckQueryDTO.pageNo},#{activeCheckQueryDTO.pageSize}
