mapper.select:
SELECT count(*)
FROM im_bonus_score_apply
WHERE user_id = #{userId}

    AND module_id = #{bonusScoreApplyPageDTO.moduleId}


    AND bonus_status = #{bonusScoreApplyPageDTO.bonusStatus}


    AND school_year = #{bonusScoreApplyPageDTO.schoolYear}

SELECT id, user_id, module_id, project_id, level_id, credit_rules_id, support_material, bonus_status, `year`, school_year, create_time, create_user, update_time, update_user FROM im_bonus_score_apply WHERE user_id = #{userId}
AND module_id = #{bonusScoreApplyPageDTO.moduleId}
AND bonus_status = #{bonusScoreApplyPageDTO.bonusStatus}
AND school_year = #{bonusScoreApplyPageDTO.schoolYear}
ORDER BY CASE bonus_status WHEN '${@com.qingfeng.cms.domain.bonus.enums.BonusStatusEnums@HAVING.name}' THEN 1 WHEN '${@com.qingfeng.cms.domain.bonus.enums.BonusStatusEnums@FAIL.name}' THEN 2 ELSE 3 END, create_time DESC LIMIT #{bonusScoreApplyPageDTO.pageNo}, #{bonusScoreApplyPageDTO.pageSize}

SELECT count(*)
FROM im_bonus_score_apply b LEFT JOIN im_score_check s ON b.id = s.score_apply_id
WHERE b.user_id in

    #{item}


    AND b.module_id = #{bonusScoreApplyPageDTO.moduleId}


    AND s.class_status = #{bonusScoreApplyPageDTO.classStatus}


    AND b.school_year = #{bonusScoreApplyPageDTO.schoolYear}

SELECT b.* FROM im_bonus_score_apply b LEFT JOIN im_score_check s ON b.id = s.score_apply_id WHERE b.user_id in
#{item}
AND b.module_id = #{bonusScoreApplyPageDTO.moduleId}
AND s.class_status = #{bonusScoreApplyPageDTO.classStatus}
AND b.school_year = #{bonusScoreApplyPageDTO.schoolYear}
ORDER BY CASE s.class_status WHEN '${@com.qingfeng.cms.domain.check.enums.CheckStatusEnums@INIT.name}' THEN 1 WHEN '${@com.qingfeng.cms.domain.check.enums.CheckStatusEnums@FAIL.name}' THEN 2 ELSE 3 END, CASE b.bonus_status WHEN '${@com.qingfeng.cms.domain.bonus.enums.BonusStatusEnums@HAVING.name}' THEN 1 WHEN '${@com.qingfeng.cms.domain.bonus.enums.BonusStatusEnums@FAIL.name}' THEN 2 ELSE 3 END, b.create_time DESC, b.school_year LIMIT #{bonusScoreApplyPageDTO.pageNo}, #{bonusScoreApplyPageDTO.pageSize}

SELECT b.* FROM im_bonus_score_apply b LEFT JOIN im_score_check s ON b.id = s.score_apply_id WHERE b.user_id in
#{item}
AND b.module_id = #{bonusScoreApplyPageDTO.moduleId}
AND s.class_status = #{bonusScoreApplyPageDTO.classStatus}
AND b.school_year = #{bonusScoreApplyPageDTO.schoolYear}
ORDER BY CASE s.college_status WHEN '${@com.qingfeng.cms.domain.check.enums.CheckStatusEnums@INIT.name}' THEN 1 WHEN '${@com.qingfeng.cms.domain.check.enums.CheckStatusEnums@FAIL.name}' THEN 2 ELSE 3 END, CASE b.bonus_status WHEN '${@com.qingfeng.cms.domain.bonus.enums.BonusStatusEnums@HAVING.name}' THEN 1 WHEN '${@com.qingfeng.cms.domain.bonus.enums.BonusStatusEnums@FAIL.name}' THEN 2 ELSE 3 END, b.create_time DESC, b.school_year LIMIT #{bonusScoreApplyPageDTO.pageNo}, #{bonusScoreApplyPageDTO.pageSize}

SELECT count(*)
FROM im_bonus_score_apply b LEFT JOIN im_score_check s ON b.id = s.score_apply_id
WHERE 1=1

    AND b.module_id = #{bonusScoreApplyPageDTO.moduleId}


    AND s.class_status = #{bonusScoreApplyPageDTO.classStatus}


    AND b.school_year = #{bonusScoreApplyPageDTO.schoolYear}

SELECT b.* FROM im_bonus_score_apply b LEFT JOIN im_score_check s ON b.id = s.score_apply_id WHERE 1=1
AND b.module_id = #{bonusScoreApplyPageDTO.moduleId}
AND s.class_status = #{bonusScoreApplyPageDTO.classStatus}
AND b.school_year = #{bonusScoreApplyPageDTO.schoolYear}
ORDER BY CASE s.student_office_status WHEN '${@com.qingfeng.cms.domain.check.enums.CheckStatusEnums@INIT.name}' THEN 1 WHEN '${@com.qingfeng.cms.domain.check.enums.CheckStatusEnums@FAIL.name}' THEN 2 ELSE 3 END, CASE b.bonus_status WHEN '${@com.qingfeng.cms.domain.bonus.enums.BonusStatusEnums@HAVING.name}' THEN 1 WHEN '${@com.qingfeng.cms.domain.bonus.enums.BonusStatusEnums@FAIL.name}' THEN 2 ELSE 3 END, b.create_time DESC, b.school_year LIMIT #{bonusScoreApplyPageDTO.pageNo}, #{bonusScoreApplyPageDTO.pageSize}
